
public class d0202 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 2; i < 3; i++) {
			for (int j = 1; j < 10; j++) {
				System.out.println(i + " x " + j + " = " + i*j);
			}
		}
		
		System.out.println("============================");
		
		int i = 2, j = 1;
		while (j < 10) {
			System.out.println(i + " x " + j + " = " + i*j);
			j++;
		}
	}

}
